mapper.select:
select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}

mapper.update:
UPDATE SETMEAL
SET STATUS = #{status}
WHERE ID = #{id}

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

SELECT *
FROM SETMEAL


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
